(214, 'Togo', 'Tourist Visa', '0.00', '0.00', '30 Days', '', '', '<div>1. Your passport, with a validity of at least another six months from the end of your stay and at least two blank pages.</div><div>2. Photocopies of the first two pages of your passport.</div><div>3. The Togo visa application form</div><div>4. Two recent passport-size pictures, with the following requirements:</div><div>5. Dimensions: 4.5 x 3.5 cm</div><div>6. White background</div><div>7. Neutral facial expression</div><div>8. Your face and head must be fully visible (headgear only accepted for religious purposes, provided it does not obstruct the face)</div><div>9. Proof you have purchased or booked a return flight ticket.</div><div>10. Travel insurance for the duration of your stay, or proof you have paid a repatriation deposit</div><div>11. Yellow Fever Certificate or the vaccination notebook</div><div>12. Proof of accommodation in Togo, such as a hotel reservation</div>'),
(215, 'Tokelau', 'Tourist Visa', '0.00', '0.00', '30 Days', '', '', '<div>1. Passport validity</div><div>2. Blank visa pages</div><div>3. Tokelau visa application form</div><div>4. Original, signed United State passport</div><div>5. (2) passport photographs</div><div>6. An itinerary of your trip</div><div>7. Proof of yellow fever vaccine</div><div><br></div>'),
(216, 'Tonga', 'Tourist Visa', '0.00', '0.00', '30 Days', '', '', '<div>1. Passport with minimum six months validity beyond intended stay and with two blank pages</div><div>2. 2 Scanned recent colour photograph. (Photo Specification);</div><div>3. Confirmed return flight ticket</div><div>4. Hotel Reservation or Invitation letter (If visiting family or friends)</div><div>5. Updated bank statement of last three months</div><div>6. Invitation letter from the host company (For Business Visa)</div><div>7. Birth Certificate for children</div><div>8. Enough funds to support the entire trip to Tanzania</div>'),
(217, 'Trinidad And Tobago', 'Tourist Visa', '0.00', '0.00', '30 Days', '', '', '<div>1. Hold a Machine Readable passport valid at least six months on entry with one blank visa page</div><div>2. Hold proof of sufficient funds in the form of a bank statement or cash</div><div>3. Hold proof of onward/return airline tickets</div><div>4. Hold documents showing proof of purpose of trip</div><div>5. Hold all documents required for the next destination</div><div>6. Hold a visa for the next destination, if required</div><div>7. Confirm with their airline that boarding will be permitted without a visa as these conditions are subject to change</div>'),
(218, 'Tunisia', 'Tourist Visa', '0.00', '0.00', '30 Days', '', '', '1. Passport<br>2. Old passport<br>3. Photo<br>4. Air ticket<br>'),
(219, 'Turkey', 'Tourist Visa', '0.00', '0.00', '30 Days', '', '', '1. Passport<div>2. Photo<br>3. Air ticket<br>4. Old passport<br>5. Travel insurance</div><div>6. Income tax returns<br>7. Covering letter from applicants<br>8. Bank statements<br>9. Hotel vocuher&nbsp;</div>'),
(220, 'Turkmenistan', 'Tourist Visa', '0.00', '0.00', '30 Days', '', '', '1. Passport<br>2. Photo<br>3. Air ticket<br>4. Old passport<br>5. Bank statement<div><br></div>');
